<?phpinclude 'conf.php';include $BACK_PATH."/init.php";// this possibly should be based on date... or a passed forecast id$hardcoded = "2012-12-10";$current_forecast_Q = $GLOBALS['TYPO3_DB']->sql(TYPO3_db, "SELECT max(id) as id, headline, severe_weather, issue_time from forecasts where issue_time = '".$hardcoded."'");while ($current_forecast = $GLOBALS['TYPO3_DB']->sql_fetch_assoc($current_forecast_Q)){	$forecast_id = $current_forecast['id'];	$headline = $current_forecast['headline'];	$severe_weather = $current_forecast['severe_weather'];	$issue_time = $current_forecast['issue_time'];}$locations_array = array();$locations = $GLOBALS['TYPO3_DB']->sql(TYPO3_db, "SELECT distinct name, forecast_locations.id, lat, lon FROM forecast_locations, forecast_detail WHERE forecast_id = " . $forecast_id . " AND forecast_detail.location_id = forecast_locations.id");while ($location = $GLOBALS['TYPO3_DB']->sql_fetch_assoc($locations)){	$location_id = $location['id'];	$location_name = $location['name'];	$location_lat = $location['lat'];	$location_lon = $location['lon'];	//echo "location: ". $location_name ." <br>";	$period_array = array();	$periods = $GLOBALS['TYPO3_DB']->sql(TYPO3_db, "SELECT distinct name, forecast_periods.id FROM forecast_periods, forecast_detail WHERE forecast_id = " . $forecast_id . " AND forecast_detail.period_id = forecast_periods.id");	while ($period = $GLOBALS['TYPO3_DB']->sql_fetch_assoc($periods)){		$period_id = $period['id'];		$period_name = $period['name'];		//echo "period: ". $period_name ." <br>";		$detail_array = array();		$forecast_details = $GLOBALS['TYPO3_DB']->sql(TYPO3_db, "SELECT * FROM forecast_detail WHERE forecast_id=" . $forecast_id . " AND period_id=".$period_id." AND location_id=".$location_id."");		while ($forecast_detail = $GLOBALS['TYPO3_DB']->sql_fetch_assoc($forecast_details)) {			$text           = $forecast_detail['text'];			$min_temp       = $forecast_detail['min_temp'];			$max_temp       = $forecast_detail['max_temp'];			$wind_speed     = $forecast_detail['wind_speed'];			$wind_direction = $forecast_detail['wind_direction'];			$icon_Q         = $GLOBALS['TYPO3_DB']->exec_SELECTquery('*', 'forecast_icons', "id='" . $forecast_detail['icon_id'] . "'");			while ($icon = $GLOBALS['TYPO3_DB']->sql_fetch_assoc($icon_Q)){				$icon_image = $icon['image'];				$icon_name = $icon['name'];			}			//echo "   detail : ".$icon_image. " " . $text . " " . $min_temp . " " . $max_temp . " " . $wind_speed . " " . $wind_direction . "<hr>";			$detail_array[] = array(				//'icon_id' => $forecast_detail['icon_id'],				'icon' => $icon_image,				'icon_name' => $icon_name,				'text' => $text,				'minTemp' => $min_temp,				'maxTemp' => $max_temp,				'windSpeed' => $wind_speed,				'windDirection' => $wind_direction,				'windSpeed' => $wind_speed			);		}		$period_array[] = array(			'id' => $period_id,			'name' => $period_name,			'detail' => $detail_array		);	}	$locations_array[] = array(		//'id' => $location_id,		'name' => $location_name,		'lat' => $location_lat,		'lon' => $location_lon,				'periods' => $period_array	);};$data = array(	//'forecast_id' => $forecast_id,	'date' => date('Y-m-d', strtotime($issue_time)),	'headline' => $headline,	'severeWeather' => $severe_weather,	'locations' => $locations_array	);$JSON_data = json_encode($data);$JSON_data_file = "../../../../fileadmin/data/latestForecast.json";$fh = fopen($JSON_data_file, 'w') or die("can't open file");fwrite($fh, $JSON_data);fclose($fh);?>