<?phpheader('Content-type: application/json');include 'conf.php';include $BACK_PATH."/init.php";$forecasts = $GLOBALS['TYPO3_DB']->exec_SELECTquery('*', 'forecasts');$forecastList = array();while ($forecast = $GLOBALS['TYPO3_DB']->sql_fetch_assoc($forecasts)) {	$forecastList[] = array(		'id' => $forecast['id'],		'rawIssueTime' => $forecast['issue_time'],		'date' => date('D jS F Y', strtotime($forecast['issue_time'])),		'headline' => $forecast['headline'],		'severeWeather' => $forecast['severe_weather']	);}$result["forecasts"] = $forecastList;echo json_encode($result);?>