<?phpheader('Content-type: application/json');include 'conf.php';include $BACK_PATH."/init.php";$name = $_GET['locName'];$lat = $_GET['locLat'];$lon = $_GET['locLon'];$insertArray = array(	'name' => $name,	'lat' => $lat,	'lon' => $lon);$res = $GLOBALS['TYPO3_DB']->exec_INSERTquery('forecast_locations', $insertArray);if ($res){	$result["result"] = true;} else {	$result["result"] = false;}echo json_encode($result);?>