<?phpheader('Content-type: application/json');include 'conf.php';include $BACK_PATH."/init.php";$locationList = array();$locations = $GLOBALS['TYPO3_DB']->exec_SELECTquery('*', 'forecast_locations');$result = array("numLocations" => $GLOBALS['TYPO3_DB']->sql_num_rows($locations));while ($location = $GLOBALS['TYPO3_DB']->sql_fetch_assoc($locations)) {	$locationList[] = array(		'id' => $location['id'],		'name' => $location['name'],		'lat' => $location['lat'],		'lon' => $location['lon']	);}$result["locations"] = $locationList;echo json_encode($result);?>